import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer,
} from 'recharts';

const Dashboard = () => {
  const navigate = useNavigate();
  const [quizStats, setQuizStats] = useState({
    quizzesTaken: 0,
    averageScore: 0,
    scores: [],
  });

  useEffect(() => {
    // Load quiz results from localStorage
    const storedResults = JSON.parse(localStorage.getItem('quizResults')) || [];
    const quizzesTaken = storedResults.length;
    const averageScore = quizzesTaken > 0
      ? storedResults.reduce((acc, cur) => acc + cur.score, 0) / quizzesTaken
      : 0;

    // Prepare data for chart: array of { name: 'Quiz 1', score: 85 }
    const scores = storedResults.map((result, index) => ({
      name: `Quiz ${index + 1}`,
      score: result.score,
    }));

    setQuizStats({ quizzesTaken, averageScore, scores });
  }, []);

  return (
    <div style={{ padding: '20px' }}>
      <h1>Dashboard</h1>
      <div style={{ marginBottom: '20px' }}>
        <button onClick={() => navigate('/')}>Home</button>{' '}
        <button onClick={() => navigate('/admin')}>Admin Panel</button>{' '}
        <button onClick={() => navigate('/quiz')}>Quiz</button>
      </div>
      <h2>Quiz Statistics</h2>
      <p>Number of quizzes taken: {quizStats.quizzesTaken}</p>
      <p>Average score: {quizStats.averageScore.toFixed(2)}%</p>
      {quizStats.scores.length > 0 ? (
        <ResponsiveContainer width="100%" height={300}>
          <BarChart
            data={quizStats.scores}
            margin={{
              top: 20, right: 30, left: 20, bottom: 5,
            }}
          >
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="name" />
            <YAxis domain={[0, 100]} />
            <Tooltip />
            <Legend />
            <Bar dataKey="score" fill="#8884d8" radius={[10, 10, 0, 0]} />
          </BarChart>
        </ResponsiveContainer>
      ) : (
        <p>No quiz results available to display.</p>
      )}
    </div>
  );
};

export default Dashboard;
